        $output = $pdoFetch->getChunk(
            $tplWrapper,
            array_merge($additionalPlaceholders, [\'output\' => $output, \'scriptProperties\' => $scriptProperties])
        );
    }

    if (!empty($toPlaceholder)) {
        $modx->setPlaceholder($toPlaceholder, $output);
    } else {
        return $output;
    }
}',
