    $where[\'active\'] = 1;
}
$select = [
    \'msProductFile\' => \'*\',
];

// Add user parameters
foreach ([\'where\'] as $v) {
    if (!empty($scriptProperties[$v])) {
        $tmp = $scriptProperties[$v];
        if (!is_array($tmp)) {
            $tmp = json_decode($tmp, true);
        }
        if (is_array($tmp)) {
