    \'payments\' => $payments,
    \'errors\' => $errors,
    \'scriptProperties\' => $scriptProperties
]);

if ($modx->user->hasSessionContext(\'mgr\') && !empty($showLog)) {
    $output .= \'<pre class="msOrderLog">\' . print_r($pdoFetch->getTime(), true) . \'</pre>\';
}

